const header=document.querySelector(".header"),toggleMenu=header.querySelector(".toggle-menu"),buttonsBlock=header.querySelector(".header__buttons-wrapper"),menu=header.querySelector(".menu");function testMenu(){toggleMenu.classList.contains("toggle-menu--hide")&&(toggleMenu.classList.remove("toggle-menu--hide"),buttonsBlock.style.justifyContent="space-between",menu.classList.add("menu--hide"))}function isEscape(e){return"Escape"===e.key}function closeMenu(e){isEscape(e)&&(getToggle(),window.removeEventListener("keydown",closeMenu))}function getToggle(){menu.classList.toggle("menu--hide"),menu.classList.toggle("menu--show"),toggleMenu.classList.toggle("toggle-menu--close")}function openMenu(e){e.preventDefault(),e.target.classList.contains("toggle-menu")&&(getToggle(),window.addEventListener("keydown",closeMenu))}testMenu(),toggleMenu.addEventListener("click",openMenu);